mapper.select:
SELECT APPROVAL_TITLE, APPROVAL_CONTENT, APPROVAL_RECOMMAND_DT, APPROVAL_TERMINATION_DT, APPROVAL_ST, APPROVAL_EMERGENCY_YN, APPROVAL_SECURITY, APPROVAL_END_DT, APPROVAL_AGENCY_YN, APPROVAL_PREDECESS_YN, EMP_DRAFTER_ID, DOC_ID, APPROVAL_CANCEL_REASON
FROM TB_APPROVAL
WHERE DOC_ID = #{docId}

SELECT DOC_ID
  FROM TB_APPROVAL_HISTORY
 WHERE EMP_ID = #{empDrafterId}

SELECT HISTORY_STEP
  FROM TB_APPROVAL_HISTORY
 WHERE EMP_ID = #{empId}
   AND DOC_ID = #{docId}

SELECT COUNT(EMP_ID)
  FROM TB_APPROVAL_SAVE
 WHERE EMP_ID = #{empId}

mapper.insert:
INSERT INTO TB_APPROVAL_HISTORY ( HISTORY_ST, EMP_ID, HISTORY_ALLOW_DT, DOC_ID, HISTORY_TURN, HISTORY_STEP
,HISTORY_SUP_ID
) VALUES ( #{historySt}, #{empId}, sysdate, #{docId}, #{historyTurn}, #{historyStep}
,#{supId}
)

INSERT INTO TB_APPROVAL_ATTACH (
		    ATTACH_ID
		   ,ATTACH_PATH
		   ,ATTACH_NAME
		   ,ATTACH_TYPE
		   ,ATTACH_CREATE_DT
		   ,EMP_ATTACHER_ID
		   ,DOC_ID
		   ,ATTACH_VOLUME
) VALUES (
		    APPROVAL_ATTACH_SEQ.NEXTVAL
		   ,#{attachPath}
		   ,#{attachName}
		   ,#{attachType}
		   ,SYSDATE
		   ,#{empAttacherId}
		   ,#{docId}
		   ,#{attachVolume}
)

mapper.update:
UPDATE TB_AGENCY
   SET AGENCY_UPDATE_DT = sysdate
       ,AGENCY_ST = #{agencySt} 
       ,AGENCY_GROUP_NAME = #{agencyGroupName} 
 WHERE EMP_AGENCY_ID = #{empAgencyId}
   AND EMP_GRANTER_ID = #{empGranterId}

UPDATE TB_APPROVAL
   SET APPROVAL_TITLE = #{approvalTitle}
       ,APPROVAL_CONTENT = #{approvalContent}
       ,APPROVAL_ST = #{approvalSt}

       ,APPROVAL_RECOMMAND_DT = sysdate


       ,APPROVAL_EMERGENCY_YN = #{approvalEmergencyYn}



 WHERE DOC_ID = #{docId}

UPDATE TB_APPROVAL
   SET APPROVAL_CANCEL_REASON = #{approvalCancelReason}
   	   ,APPROVAL_CONTENT = #{approvalContent}
   	   ,APPROVAL_ST = #{approvalSt}
 WHERE DOC_ID = #{docId}

UPDATE TB_APPROVAL_HISTORY
   SET HISTORY_ST = #{historySt}
       ,HISTORY_ALLOW_DT = sysdate
       ,HISTORY_TURN = #{historyTurn}
 WHERE DOC_ID = #{docId}
   AND EMP_ID = #{empId}

UPDATE TB_APPROVAL_HISTORY
   SET HISTORY_TURN = #{historyTurn}
 WHERE DOC_ID = #{docId}
   AND HISTORY_STEP = #{historyStep}

UPDATE TB_AGENCY
   SET AGENCY_ST = 0
 WHERE EMP_GRANTER_ID = #{empGranterId}

UPDATE TB_APPROVAL
   SET APPROVAL_ST = 4
       ,APPROVAL_CONTENT = #{approvalContent}
       ,APPROVAL_TITLE = #{approvalTitle}
 WHERE DOC_ID = #{docId}
   AND EMP_DRAFTER_ID = #{empDrafterId}

mapper.delete:
delete 
  from tb_approval_save
 where form_id = #{formId}
   and emp_id = #{empId}
